function copyToClipboard(e,t){navigator.clipboard.writeText(e).then(()=>{const e=t.textContent;t.textContent="Copied!",t.classList.add("copied"),setTimeout(()=>{t.textContent=e,t.classList.remove("copied")},2e3)})}function escapeHtml(e){const t=document.createElement("div");return t.textContent=e,t.innerHTML}function copyCommand(){const e=document.getElementById("installCommand").textContent,t=event.target;copyToClipboard(e,t)}