function updateProvider(){const t=document.getElementById("providerSelect").value;currentProvider=t;const e=document.getElementById("modelSelect"),n=document.getElementById("modelInput");if(t==="none")e.style.display="block",n.style.display="none",e.innerHTML='<option value="">None/Default</option>',e.disabled=!0,currentModel=null;else if(t==="other")e.style.display="none",n.style.display="block",e.disabled=!1,n.focus(),currentModel=n.value;else{e.style.display="block",n.style.display="none",e.disabled=!1;const s=window.providerData[t].models;e.innerHTML='<option value="none">None/Default</option>'+s.map(e=>`<option value="${e}">${e}</option>`).join(""),currentModel=getModelString(t,"none")}renderAllPrompts()}function updateModel(){const e=document.getElementById("modelSelect"),t=document.getElementById("modelInput");if(currentProvider==="other")currentModel=t.value;else{const t=e.value;currentModel=getModelString(currentProvider,t)}renderAllPrompts()}function getModelString(e,t){return t=="none"?e:`${e}/${t}`}